extern bool SaveBMPFile(char *filename, HBITMAP bitmap, HDC bitmapDC, int width, int height){
    HBITMAP OffscrBmp=NULL;
    HDC OffscrDC=NULL;
    LPBITMAPINFO lpbi=NULL;
    LPVOID lpvBits=NULL;
    HANDLE BmpFile=INVALID_HANDLE_VALUE;
    BITMAPFILEHEADER bmfh;

    DeleteFile(filename);

    if ((OffscrBmp = CreateCompatibleBitmap(bitmapDC, width, height)) == NULL)
        return false;

    if ((OffscrDC = CreateCompatibleDC(bitmapDC)) == NULL)
		return false;

    HBITMAP OldBmp = (HBITMAP)SelectObject(OffscrDC, OffscrBmp);

    BitBlt(OffscrDC, 0, 0, width, height, bitmapDC, 0, 0, SRCCOPY);

    lpbi = (LPBITMAPINFO) malloc((sizeof(BITMAPINFOHEADER) + 256 * sizeof(RGBQUAD))*sizeof(char));

    ZeroMemory(&lpbi->bmiHeader, sizeof(BITMAPINFOHEADER));
    lpbi->bmiHeader.biSize = sizeof(BITMAPINFOHEADER);

    SelectObject(OffscrDC, OldBmp);
    if (!GetDIBits(OffscrDC, OffscrBmp, 0, height, NULL, lpbi, DIB_RGB_COLORS))
        return false;

    lpvBits = malloc(sizeof(char)*lpbi->bmiHeader.biSizeImage);

    if (!GetDIBits(OffscrDC, OffscrBmp, 0, height, lpvBits, lpbi, DIB_RGB_COLORS))
		return false;

    if ((BmpFile = CreateFile(filename,
                              GENERIC_WRITE,
                              0, NULL,
                              CREATE_ALWAYS,
                              FILE_ATTRIBUTE_NORMAL,
                              NULL)) == INVALID_HANDLE_VALUE)

							  return false;

    DWORD Written;

    bmfh.bfType = 19778;
    bmfh.bfReserved1 = bmfh.bfReserved2 = 0;
    if (!WriteFile(BmpFile, &bmfh, sizeof(bmfh), &Written, NULL))
        return false;

    if (Written < sizeof(bmfh))
		return false;

    if (!WriteFile(BmpFile, &lpbi->bmiHeader, sizeof(BITMAPINFOHEADER), &Written, NULL))
		return false;

    if (Written < sizeof(BITMAPINFOHEADER))
			return false;
    int PalEntries;
    if (lpbi->bmiHeader.biCompression == BI_BITFIELDS)
		PalEntries = 3;
    else
        PalEntries = (lpbi->bmiHeader.biBitCount <= 8) ?
            (int)(1 << lpbi->bmiHeader.biBitCount)
        : 0;
    if(lpbi->bmiHeader.biClrUsed)
		PalEntries = lpbi->bmiHeader.biClrUsed;

    if(PalEntries){
        if (!WriteFile(BmpFile, &lpbi->bmiColors, PalEntries * sizeof(RGBQUAD), &Written, NULL))
			return false;

        if (Written < PalEntries * sizeof(RGBQUAD))
			return false;
		}

    bmfh.bfOffBits = GetFilePointer(BmpFile);

    if (!WriteFile(BmpFile, lpvBits, lpbi->bmiHeader.biSizeImage, &Written, NULL))
		return false;

    if (Written < lpbi->bmiHeader.biSizeImage)
		return false;

    bmfh.bfSize = GetFilePointer(BmpFile);

    SetFilePointer(BmpFile, 0, 0, FILE_BEGIN);
    if (!WriteFile(BmpFile, &bmfh, sizeof(bmfh), &Written, NULL))
        return false;

    if (Written < sizeof(bmfh))
		return false;

    CloseHandle(BmpFile);
    free(lpvBits);
    free(lpbi);

    return true;
}
